mapper.select:
    select 
    	
    from t_article ta
	    
	    	
	    		ta.category_id = #{categoryId,jdbcType=INTEGER}
	    	
	    	
	    		AND ta.title like CONCAT("%",#{title,jdbcType=VARCHAR},"%") 
	    	
	    
	    ORDER BY ta.create_time DESC
    LIMIT #{page.pageIndex,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER};
